import React from "react";
import { Card, Stack } from "react-bootstrap";
import Products from "./Product";
// import '../CSS/Index.css'
import { Link, } from "react-router-dom";
import { useState, useEffect } from "react";
import Login from "./Login";
import Register from "./Register";


const CardProduct = () => {
  const [modalRegisterShow, setModalRegisterShow] = useState(false);
  const [modalLoginShow, setModalLoginShow] = useState(false); 
  const [loggedIn, setLoggedIn] = useState(null);

  const reRenderProduct = () => {
    setLoggedIn(!!localStorage.getItem("LOGIN_STATUS"));
  };

  useEffect(() => {
    reRenderProduct();
  }, []);

  <Login reRenderProduct={reRenderProduct} />;

    return (
    <Stack direction="horizontal" gap={3} className='' style={{margin: "50px auto 20px", width: "1072px", height: "392px"}} >

        {/* card */}
        {Products.map((Product) => (
        <Card style={{ width: '18rem', borderRadius: '13px', background: "#F7DADA", border: "none", textDecoration:"none" }} key={Product.id}>
        {loggedIn ? (
        <Link to={'/product/' + Product.id}>
        <Card.Img variant="top" src={Product.image} />
          <Card.Body >
            <Card.Title style={{ color: "#BD0707", fontSize: "18px", }}><b>{Product.title}</b></Card.Title>
            <Card.Text style={{ fontSize: "14px" }}>
                Rp. {Product.price}
            </Card.Text>
          </Card.Body>
        </Link>
       ) : (
        <div onClick={setModalLoginShow}>
          <Card.Img variant="top" src={Product.image} />
          <Card.Body>
            <Card.Title style={{ color: "#BD0707", fontSize: "18px" }}>
              <b> {Product.title}</b>
            </Card.Title>
            <Card.Text style={{ fontSize: "14px" }}>
              Rp. {Product.price}
            </Card.Text>
          </Card.Body>
        </div>
      )}
        </Card>
      ))}
      <Login
        show={modalLoginShow}
        Hide={() => setModalLoginShow(false)}
        setModalLoginShow={setModalLoginShow}
        setModalRegisterShow={setModalRegisterShow}

      />
      <Register
        show={modalRegisterShow}
        Hide={() => setModalRegisterShow(false)}
        setModalLoginShow={setModalLoginShow}
        setModalRegisterShow={setModalRegisterShow}
      />
    </Stack>
    );
    };

export default CardProduct
