import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Modal from 'react-bootstrap/Modal';
import React, { useState } from 'react';
// import Register from './Register';


function Login(props){
  const [emaillog, setEmaillog] = useState(" ");
  const [passwordlog, setPasswordlog] = useState(" ");
  const [flag, setFlag] = useState(false);
  const [home, setHome] = useState(true);
  const [login, setLogin] = useState(true);

  function handleLogin(e) {
    e.preventDefault();
    let password = localStorage
      .getItem("password")
      .replace(/"/g, "");
    let email = localStorage.getItem("email").replace(/"/g, "");


    if (!emaillog || !passwordlog) {
      setFlag(true);
      console.log("EMPTY");
    } else if (passwordlog !== password || emaillog !== email) {
      setFlag(true);
    } else {
      setHome(!home);
      setFlag(false);
    }
    setLogin(!login);
  }

  function handleClick() {
    setLogin(!login);
}


    return(
        <>

        <Modal {...props}>

        <div className="form-group">

            <h1>
                Login
            </h1>

            <Form onSubmit={{handleLogin}}>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Control onChange={(event) => setEmaillog(event.target.value)} type="text" placeholder="Enter email" name='email'/>
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Control onChange={(event) => setPasswordlog(event.target.value)} type="password" placeholder="Password" name='password' />
                </Form.Group>

                <Button onClick={handleClick} variant="danger" type="submit">
                    Login
                </Button>

                <p>
                <Button onClick={props.onRegis} variant="link">Don't hava an account? Klick <span>Here</span></Button>
                </p>
            </Form>

         </div>    

        </Modal>


        </>


    );
}

export default Login
