import React from "react";
import { Card, Stack } from "react-bootstrap";
// import '../CSS/Index.css'
import { Link, } from "react-router-dom";
import { useState} from "react";
import Login from "./Auth/Login";
import Register from "./Auth/Register";
import { API } from "../config/api";
import { useQuery, useMutation } from 'react-query';
import { useContext } from "react";
import { UserContext } from "../context/userContext";


const CardProduct = () => {
  const [modalRegisterShow, setModalRegisterShow] = useState(false);
  const [modalLoginShow, setModalLoginShow] = useState(false); 
  const [state] = useContext(UserContext);
  const isLogin = !state.isLogin;


  let { data: products } = useQuery('productsCache', async () => {
    const response = await API.get('/products');
    return response.data.data;
  });

    return (
    <Stack direction="horizontal" gap={3} className='' style={{margin: "50px auto 20px", width: "1072px", height: "392px"}} >

        {/* card */}

        {products.map((item,index) => (
        <Card style={{ width: '18rem', borderRadius: '13px', background: "#F7DADA", border: "none", textDecoration:"none" }} key={index} item={item}>
        {products?.length !== 0 ? (
        <Link to={'/product/' + index}>
        <Card.Img variant="top" src={item.image} />
          <Card.Body >
            <Card.Title style={{ color: "#BD0707", fontSize: "18px", }}><b>{item.title}</b></Card.Title>
            <Card.Text style={{ fontSize: "14px" }}>
                Rp. {item.price}
            </Card.Text>
          </Card.Body>
        </Link>
       ) : (
        <div onClick={setModalLoginShow}>
          <Card.Img variant="top" src={item.image} />
          <Card.Body>
            <Card.Title style={{ color: "#BD0707", fontSize: "18px" }}>
              <b> {item.title}</b>
            </Card.Title>
            <Card.Text style={{ fontSize: "14px" }}>
              Rp. {item.price}
            </Card.Text>
          </Card.Body>
        </div>
      )}
        </Card>
      ))}
      <Login
        show={modalLoginShow}
        Hide={() => setModalLoginShow(false)}
        setModalLoginShow={setModalLoginShow}
        setModalRegisterShow={setModalRegisterShow}

      />
      <Register
        show={modalRegisterShow}
        Hide={() => setModalRegisterShow(false)}
        setModalLoginShow={setModalLoginShow}
        setModalRegisterShow={setModalRegisterShow}
      />
    </Stack>
    );
    };

export default CardProduct
