/* eslint-disable react-hooks/exhaustive-deps */
import Home  from './pages/Home';
import { BrowserRouter as Router, Route, Routes, useNavigate, useState } from 'react-router-dom'
import './CSS/Login.css';
import Profile from './pages/Profile';
import ProductDetail from './pages/ProductDetail';
import NavbarBucks from './components/NavbarBucks';
import Cart from './pages/Cart'
import AddProduct from './pages/AddProduct';
import AddTopping from './pages/AddTopping';
import { API, setAuthToken } from "./config/api";
import { useContext, useEffect } from "react";
import { UserContext } from "../src/context/userContext";



if (localStorage.token) {
  setAuthToken(localStorage.token)
}

function App() {
  let navigate = useNavigate();

  const [state, dispatch] = useContext(UserContext);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Redirect Auth
    if (localStorage.token) {
      setAuthToken(localStorage.token);
    }

    if (state.isLogin === false && !isLoading) {
      navigate("/auth");
    }
  }, [state]);

  const checkUser = async () => {
    try {
      const response = await API.get("/check-auth");

      // If the token incorrect
      if (response.status === 404) {
        return dispatch({
          type: "AUTH_ERROR",
        });
      }

      console.log("response check auth", response);

      // Get user data
      let payload = response.data.data;
      // Get token from local storage
      payload.token = localStorage.token;

      // Send data to useContext
      dispatch({
        type: "USER_SUCCESS",
        payload,
      });
      console.log("ini data state", state);
      setIsLoading(false);
    } catch (error) {
      console.log(error);
      setIsLoading(false);
    }
  };

  useEffect(() => {
    checkUser();
  }, []);

  useEffect(() => {
    // Redirect Auth
    if (localStorage.token) {
      setAuthToken(localStorage.token);
    }

    if (state.isLogin === false && !isLoading) {
      navigate("/");
    }
  }, [state]);

  return (
    <Router>
    <div>
      <NavbarBucks/>
    </div>

    <Routes>
      <Route exact path="/" element={<Home />} />
      <Route exact path="/profile" element={<Profile />} />
      <Route exact path="/product/:id" element={<ProductDetail />} />
      <Route exact path="/cart" element= {<Cart/>} />
      <Route exact path="/addproduct" element= {<AddProduct/>} />
      <Route exact path="/addtopping" element= {<AddTopping/>} />
    </Routes>
  </Router>
  );

}

export default App;
