import React, { useState } from "react";
import { Form} from "react-bootstrap";
import Login from "./Login";
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Loginmodal from './Loginmodal'

function Registration() {
  // const [name, setName] = useState("");
  // const [email, setEmail] = useState("");
  // const [password, setPassword] = useState("");
  // const [phone, setPhone] = useState("");
  // const [profession, setProfession] = useState("");

  // const [flag, setFlag] = useState(false);
  const [rise, setRise] = useState(false);
  const handleClose = () => setRise(false);
  const handleRise = () => setRise(true);
  const [login, setLogin] = useState(true);



  // function handleFormSubmit(e) {
  //   e.preventDefault();

  //   if (!name || !email || !password || !phone || !profession) {
  //     setFlag(true);
  //   } else {
  //     setFlag(false);
  //     localStorage.setItem("sanskarEmail", JSON.stringify(email));
  //     localStorage.setItem(
  //       "sanskarPassword",
  //       JSON.stringify(password)
  //     );
  //     console.log("Saved in Local Storage");

  //     setLogin(!login);
  //   }
  // }

  function handleClick() {
    setLogin(!login);
  }




  return (
    <>

        <Button variant="danger" onClick={handleRise}>
            Register
        </Button>
        <div>
          {" "}
          {login ? (
            <Modal  show={rise} onHide={handleClose}>

            <div className="form-group">

                <h1>
                    Register
                </h1>

                <Form>
                    <Form.Group className="mb-3" controlId="formBasicEmail">
                        <Form.Control type="email" placeholder="Enter email"/>
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="formBasicPassword">
                        <Form.Control type="password" placeholder="Password" />
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="formBasicFullName">
                        <Form.Control type="text" placeholder="Full Name"/>
                    </Form.Group>

                    <Button variant="danger" type="submit">
                        Register
                    </Button>

                    <p>
                    <Button variant="link" onClick={handleClick}>Already have an account? <span>Here</span></Button>
                    </p>

                    {/* {flag && (
                    <Alert color="primary" variant="danger">
                      I got it you are in hurry! But every Field is important!
                    </Alert>
                  )} */}
                </Form>

             </div>    

            </Modal>

          ) : (
            <Loginmodal/>
          )}
        </div>

    </>
  );
}

export default Registration;
