/* eslint-disable jsx-a11y/alt-text */
import React, { useParams } from "react-router-dom";
import Products from "./Product";
import {Container, Card }from 'react-bootstrap';
import Topping from "./Topping";
import Stack from 'react-bootstrap/Stack';



 const ProductDetail = () => {
    const {id} = useParams() //int
    const dataProduct = []
    for (
        let index = 0; index<Products.length;index++
    ) {
        if(Number(Products[index].id )=== Number(id)){ //object
            dataProduct.push(Products[index])
        }
    }
    console.log(dataProduct)
    return (
        <>
        <Container className="mt-5 d-flex">
        <div>
            <img src = {dataProduct[0].image} />
        </div>

        <div className="ms-5">
            <div>
             <h1>{dataProduct[0].title}</h1>
            </div>

            <div>
            Rp. {dataProduct[0].price}
            </div>

            <div className="mt-3">
                Topping
                {Topping.map((Topping)=>(
                    <Stack direction="horizontal" gap={3}>
                    <Card style={{ width: '5rem', borderRadius: '13px', border: "none",  }} key={Topping.id}>

                    <Card.Img variant="top" src={Topping.image} />
                    <Card.Body >
                    <Card.Title style={{ color: "#BD0707", fontSize: "18px", }}><b>{Topping.title}</b></Card.Title>

                    </Card.Body>
                    </Card>
                    </Stack>

                ))}
            </div>
        </div>


        </Container>
        </>


        )

}

export default ProductDetail
