import React, { useState } from "react";
import { Button, Card, Container, Form } from "react-bootstrap";
import Stack from "react-bootstrap/Stack";
import Vector from "../img/Vector.png"
import { Popup } from "../components/PopUp"
import { API } from  "../config/api"
import { useMutation, useQuery } from 'react-query'
import NavbarBucks from "../components/NavbarBucks";
import { useNavigate } from "react-router-dom";

function Cart() {
  const [modalPopShow, setModalPopShow] = useState(false);

  let navigate = useNavigate();

  // cart
  let { data: cart, refetch } = useQuery("cartsCache", async () => {
    const response = await API.get("/carts-id");
    return response.data.data;
  });

  // subtotal
  let resultTotal = cart?.reduce((a, b) => {
    return a + b.subtotal;
  }, 0);

  // remove
  let handleDelete = async (id) => {
    await API.delete(`/cart/` + id);
    refetch();
  };

  // pay
  const form = {
    status: "pending",
    total: resultTotal,
  };

  const [formP, setForm] = useState({
    address: "",
    postal_code: "",
    image: "",
  });

  const [previewName, setPreviewName] = useState(""); //name
  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]:
        e.target.type === "file" ? e.target.files : e.target.value,
    });
    // Create image url for preview
    if (e.target.type === "file") {
      setPreviewName(e.target.files[0].name);
    }
  };

  const handleSubmit = useMutation(async (e) => {
    const config = {
      headers: {
        "Content-type": "application/json",
      },
    };



    // Insert transaction data
    const body = JSON.stringify(form);
    const response = await API.patch("/transaction", body, config);
    await API.patch("/cart", body, config);
    // const response = await API.patch("/transaction", body, config);
    // const token = response.data.data.token;

    // window.snap.pay(token, {
    //   onSuccess: function (result) {
    //     console.log(result);
    //     navigate("/profile");
    //   },
    //   onPending: function (result) {
    //     console.log(result);
    //     navigate("/profile");
    //   },
    //   onError: function (result) {
    //     console.log(result);
    //   },
    //   onClose: function () {
    //     alert("you closed the popup without finishing the payment");
    //   },
    // });

  });
  //

  // useEffect(() => {
  //   //change this to the script source you want to load, for example this is snap.js sandbox env
  //   const midtransScriptUrl = "https://app.sandbox.midtrans.com/snap/snap.js";
  //   //change this according to your client-key
  //   const myMidtransClientKey = process.env.REACT_APP_MIDTRANS_CLIENT_KEY;

  //   let scriptTag = document.createElement("script");
  //   scriptTag.src = midtransScriptUrl;
  //   // optional if you want to set script attribute
  //   // for example snap.js have data-client-key attribute
  //   scriptTag.setAttribute("data-client-key", myMidtransClientKey);

  //   document.body.appendChild(scriptTag);
  //   return () => {
  //     document.body.removeChild(scriptTag);
  //   };
  // }, []);



  return (
    <>
    <NavbarBucks/>
    <Container>
      <h1
        style={{
          color: "#BD0707",
          marginLeft: "3px",
          marginTop: "20px",
          fontSize: "20px",
        }}
      >
        My Cart
      </h1>
      <p
        style={{
          color: "#BD0707",
          marginLeft: "3px",
          marginTop: "20px",
          fontSize: "20px",
        }}
      >
        Review Your Order
      </p>
      <hr style={{ width: "760px" }}></hr>
        <div>
        {cart?.map((item, index)=>(
        <Stack direction="horizontal" gap={3} key={index} style={{marginTop:"10px"}}>
          <img src={item?.product?.image} alt="" style={{width:"5rem"}}/>
            <div>
              <Card.Title style={{ color: "#BD0707", fontSize: "25px" }}>
                {item.product.title}
              </Card.Title>

              <p
                style={{ fontSize: "15px", color: "#BD0707", marginTop: "10px" }}
              >
                <strong style={{ color: "#974A4A" }}>Topping</strong> : {" "}
                <span>
                    {" "}
                    {item.topping?.map((topping, idx) => (
                      <span className="d-inline" key={idx}>
                        {topping.title},
                      </span>
                    ))}
                </span>
              </p>
            </div>
            <div
              style={{
                right: "50rem",
                position: "absolute",

              }}
            >
                <p>Rp. {item?.subtotal}</p>
                <img
                  src={Vector} alt=''
                  onClick={() => handleDelete(item.id)}
                  style={{
                    position: "absolute",
                    right: "3px",
                  }}
                ></img>
              </div>
          </Stack>
          ))}
          </div>
      <hr style={{ width: "760px" }}></hr>

      <hr style={{ width: "330px", marginTop: "50px" }}></hr>

      <Stack direction="horizontal" gap={3}>
        <p style={{ color: "#BD0707" }}>Subtotal</p>
        <p
          style={{
            position: "absolute",
            right: "67rem",
            color: "#BD0707",
          }}
        >
         Rp.{resultTotal}
        </p>
      </Stack>

      <Stack direction="horizontal" gap={3}>
        <p style={{ color: "#BD0707" }}>Qty</p>
        <p
          style={{
            position: "absolute",
            right: "66rem",
            color: "#BD0707",
          }}
        >
          {cart?.length}
        </p>
      </Stack>

      <hr style={{ width: "330px", marginTop: "20px" }}></hr>

      <Stack direction="horizontal" gap={3}>
        <p style={{ color: "#BD0707", fontWeight: "bold" }}>Total</p>
        <p
          style={{
            position: "absolute",
            right: "67rem",
            color: "#BD0707",
            fontWeight: "bold",
          }}
        >
          Rp. {resultTotal}
        </p>
      </Stack>



      <Form 
        style={{
          width: "350px",
          position: "absolute",
          right: "10rem",
          top: "12rem",
        }}
      >
      <form onSubmit={(e) => handleSubmit.mutate(e)}>
          <div className="authContainer">
            <h1 className="authTitle">Edit Profile</h1>
            <input
              type="text"
              className="inputAuth p-2"
              placeholder="Address"
              name="address"
              id="address"
              onChange={handleChange}
            />
            <input
              type="number"
              className="inputAuth p-2"
              placeholder="Postal Code"
              name="postal_code"
              id="postalcode"
              onChange={handleChange}
            />
            <input
              type="file"
              name="image"
              id="addProductImage"
              hidden
              onChange={handleChange}
            />
            <label
              htmlFor="addProductImage"
              className={
                previewName === "" ? "addProfileImage" : "previewPhoto"
              }
            >
              {previewName === "" ? "Photo Profile" : previewName}
              <img src={paperClip} alt="paperClip" />
            </label>

            <button className="btnAuth mb-4">Submit</button>
          </div>
        </form>

      </Form>
    </Container>
    </>
  )
}

export default Cart;
