/* eslint-disable jsx-a11y/alt-text */
// import Container from 'react-bootstrap/Container';
// import Navbar from 'react-bootstrap/Navbar';
import '../CSS/Index.css'
// import Login from './Auth/Login';
// import Register from './Auth/Register';
import '../CSS/Login.css'
// import logo from '../img/logo.png';
import Button from 'react-bootstrap/Button';
import React, { useState, useRef } from 'react';
import Overlay from 'react-bootstrap/Overlay';
import Popover from 'react-bootstrap/Popover';
import Profil from '../img/Profile.png'
import LogOut from '../img/Logout.png'
import { Link, useNavigate } from 'react-router-dom';
import Badge from 'react-bootstrap/Badge'


export default function ModalAuth({navUse,setnavUse}) {
  const [muncul, setShow] = useState(false);
  const [target, setTarget] = useState(null);
  const ref = useRef(null);
  const handleClick = (event) => {
    setShow(!muncul);
    setTarget(event.target);

  };
  const navigate = useNavigate();
  const handleLogout = () => {
    localStorage.removeItem("LOGIN_STATUS");
    navigate ("/");
    window.location.reload()

  };
  return (
    <div show={navUse} setShow={setnavUse}>
    <div className='d-flex'>
      <div className='d-flex align-items-center'>
        <Link to={"/cart"}>
          <img src = {require ('../img/cart.png')} className="" width="30" height="30" />
          <Badge bg="danger">9</Badge>
        </Link>
      </div>


      <div ref={ref}>

      <Button className='bg-white border border-0' onClick={handleClick}><img src = {require ('../img/Ellipse 1.png')} className="userImg" width="50" height="50" /></Button>
      <Overlay
        show={muncul}
        target={target}
        placement="bottom-end"
        container={ref}
        containerPadding={20}
      >
        <Popover id="popover-contained" className='popover' >
          <Popover.Body>
          <button className='border border-0 bg-white' > 
            <Link to="/profile">
              <img src={Profil} alt=''></img>               
            </Link>
          </button> 

          </Popover.Body>
          <hr></hr>
          <Popover.Body>
          <button className='border border-0 bg-white' onClick={handleLogout}  > 
            <img src={LogOut} alt=''></img>  
          </button>
          </Popover.Body>
        </Popover>
      </Overlay>
    </div>
    </div>
    </div>
  )
}
