/* eslint-disable jsx-a11y/alt-text */
import React, { useParams } from "react-router-dom";
import Products from "../components/Product";
import {Container, Card, Button, }from 'react-bootstrap';
// import item from "../components/item";
import '../CSS/Index.css';
import { useState } from "react";
import Topping from "../components/Topping"


 export default  function ProductDetail() {
    const {id} = useParams() //int
    const dataProduct = []
    for (
        let index = 0; index<Products.length;index++
    ) {
        if(Number(Products[index].id )=== Number(id)){ //object
            dataProduct.push(Products[index])
        }
    }
    console.log(dataProduct)

    const getFormattedPrice = (price) => `${price.toFixed(0)}`;

    // const [isChecked, setIsChecked] = useState(false);

    const [checkedState, setCheckedState] = useState(
        new Array(Topping.length).fill(false)
    );
    const [total, setTotal] = useState(0);


    const handleOnChange = (position) => {
        const updatedCheckedState = checkedState.map((item, index) =>
          index === position ? !item : item
        );
        setCheckedState(updatedCheckedState);

        const totalPrice = updatedCheckedState.reduce(
          (sum, currentState, index, ) => {
            if (currentState === true) {
              return sum + Topping[index].price 
            }
            return sum;
          },
          0 
        );

        setTotal(totalPrice);
      };



    return (
        <>

        <Container className="mt-5 ps-5 d-flex">
        <div>
            <img src = {dataProduct[0].image} className="coffee" />

        </div>

        <div className="ms-5">
            <div>
             <h1 className="titleProduct">{dataProduct[0].title}</h1>
            </div>

            <div className="priceProduct">
            Rp. {dataProduct[0].price}
            </div>

            <div className="mt-3">
                <div className="T">Toping</div>
                <>
                <div className="Topping">
                {Topping.map((Topping, index)=>(
                <Card className="cardtopping" key={id} >


                <Card.Img className="topimg" src={Topping.image} />
                <input type="checkbox" id={`custom-checkbox-${id}`}  checked={checkedState[index]} onChange={() => handleOnChange(index)} />

                <Card.Body >
                <Card.Title className="titletopping" style={{ color: "#BD0707", }}>{Topping.title}</Card.Title>

                </Card.Body>
                </Card>
                ))}
                </div>
                </>
            </div>

            {/* <div>{getFormattedPrice(price)}</div> */}

            <div className="Total">
             <div>Total</div>
             <div>Rp. `${dataProduct[0].price}` + `${getFormattedPrice(total)}` </div>
            </div>

            <div>
            <Button className="AddCart" variant="danger">Add Cart</Button>
            </div>


        </div>


        </Container>
        </>


        )

}
