/* eslint-disable jsx-a11y/alt-text */
import React, { useParams } from "react-router-dom";
import Products from "../components/Product";
import {Container, Card, Button, Overlay, }from 'react-bootstrap';
import Topping from "../components/Topping"
import '../CSS/Index.css'
import {useState, useRef} from  "react"




 const ProductDetail = () => {
    const {id} = useParams() //int
    const dataProduct = []
    for (
        let index = 0; index<Products.length;index++
    ) {
        if(Number(Products[index].id )=== Number(id)){ //object
            dataProduct.push(Products[index])
        }
    }
    console.log(dataProduct)
    const [show, setShow] = useState(false);
    const [target, setTarget] = useState(null);
    const ref = useRef(null);
    const handleClick = (event) => {
        setShow(!show);
        setTarget(event.target);
      };


    return (
        <>

        <Container className="mt-5 ps-5 d-flex">
        <div ref={ref}>
            <img src = {dataProduct[0].image} className="coffee" onClick={handleClick}/>
            <Overlay
                show={show}
                target={target}
                placement="bottom"
                container={ref}
                containerPadding={20}
            >
                <Popover id="popover-contained">
                <Popover.Header as="h3">Popover bottom</Popover.Header>
                <Popover.Body>
                    <strong>Holy guacamole!</strong> Check this info.
                </Popover.Body>
                </Popover>
            </Overlay>
        </div>

        <div className="ms-5">
            <div>
             <h1 className="titleProduct">{dataProduct[0].title}</h1>
            </div>

            <div className="priceProduct">
            Rp. {dataProduct[0].price}
            </div>

            <div className="mt-3">
                <div className="T">Toping</div>
                <>
                <div className="Topping">
                {Topping.map((Topping)=>(
                <Card className="cardtopping" key={Topping.id}>

                <Card.Img className="topimg" src={Topping.image} />
                <Card.Body >
                <Card.Title className="titletopping" style={{ color: "#BD0707", }}>{Topping.title}</Card.Title>

                </Card.Body>
                </Card>
                ))}
                </div>
                </>
            </div>

            <div className="Total">
             <div>Total</div>
             <div>Rp. {dataProduct[0].price}</div>
            </div>

            <div>
            <Button className="AddCart" variant="danger">Add Cart</Button>
            </div>


        </div>


        </Container>
        </>


        )

}

export default ProductDetail
