import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Modal from 'react-bootstrap/Modal';
import React, { useState, useContext } from 'react';
import { UserContext } from '../../context/userContext';
import { useNavigate } from 'react-router-dom';
import { Alert } from 'react-bootstrap';
import { useMutation } from 'react-query';
import "../../CSS/Login.css"
import { API } from '../../config/api';

const Login = ({show,Hide, setModalRegisterShow,setModalLoginShow}) => {
  const [state, dispatch] = useContext(UserContext);
  const [form, setForm] = useState({
    email: "",
    password: "",
  });

  //
  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = useMutation(async (e) => {
    try {
      e.preventDefault();

      const config = {
        headers: {
          "Content-type": "application/json",
        },
      };

      const body = JSON.stringify(form);

      const response = await API.post("/login", body, config);

      dispatch({
        type: "LOGIN_SUCCESS",
        payload: response.data.data,
      });

      setShow(false);
    } catch (error) {
      console.log(error);
    }
  });



  // const users = []

    // const [userLogin, setState] = useState({
    //   email: "",
    //   password: "",
    // })

    // let storage = JSON.parse(localStorage.getItem("DATA_USER"))

    // const handleOnSubmit = (e) => {
    //   e.preventDefault();
    //   storage.forEach(element => {
    //     if (userLogin.email === element.email && userLogin.password === element.password ) {
    //       users.push(userLogin)
    //       localStorage.setItem("LOGIN_STATUS", JSON.stringify(users))
    //       setModalLoginShow(false)
    //       window.location.reload(true)
    //     } else {
    //       console.log(users)
    //     }


    //   });
    // } 


    return(
        <>

        <Modal show={show} onHide={Hide}>

        <div className="form-group">

            <h1>
                Login
            </h1>

            <Form onSubmit={(e) => handleSubmit.mutate(e)}>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Control onChange={handleChange} type="email" placeholder="Enter email" name='email' value={email} />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Control onChange={handleChange} type="password" placeholder="Password" name='password' value={password} />
                </Form.Group>

                <Button variant="danger" type="submit">
                    Login
                </Button>

                <p>
                <Button onClick={() => {setModalLoginShow(false); setModalRegisterShow(true)}} variant="link">Don't hava an account? Klick <span>Here</span></Button>
                </p>
            </Form>

         </div>    

        </Modal>

        </>


    );
}

export default Login
