/* eslint-disable jsx-a11y/alt-text */
import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import '../CSS/Index.css'
import Login from './Auth/Login';
import Register from './Auth/Register';
import '../CSS/Login.css'
import logo from '../img/logo.png';
import Button from 'react-bootstrap/Button';
import React, { useState } from 'react';
// import { API } from '../config/api';
import { useContext } from "react";
import { UserContext } from "../context/userContext";
import ModalAuth from '../components/ModalAuth'
import { Link } from 'react-router-dom';
import Cart from "../img/cart.png"
import Dropdown from './dropdown';


const NavbarBucks = ({ setShows, shows }) => {
  const [state] = useContext(UserContext);
  const isLogin = !state.isLogin;
  const [bubble, setBubble] = useState([]);

  useEffect(() => {
    API.get("/carts-id")
      .then((res) => {
        setBubble(res.data.data);
      })
      .catch((err) => console.log("error", err));
  });


  return (

      <Navbar>
        <Container>
          <Navbar.Brand>
          <Link to={state.user.status === "user" ? "/" : "/transaction"}>
            <img
              alt="logo"
              src={logo}
              width="70"
              height="70"
              className="d-inline-block align-top"
            />
          </Link>
          </Navbar.Brand>
          {isLogin ? (
           <div className="navbarRight">
           <div
             className={
               bubble === undefined
                 ? "d-none"
                 : bubble?.length === 0
                 ? "d-none"
                 : "circle"
             }
           >
             {bubble?.length}
           </div>
           <Link to={"/cart"}>
             <img
               src={Cart}
               alt="cart"
               className={
                 state.user.status === "customer" ? "navbarCart" : "d-none"
               }
             />
           </Link>
           <Dropdown />
         </div>
           ):(
            <div className="navbarLeft">
            <ModalAuth show={shows} setShow={setShows} />
            </div>
          )}
      </Container>
      </Navbar>

  );
}

export default NavbarBucks ;
