import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Modal from 'react-bootstrap/Modal';
import React, { useState, useEffect } from 'react'





function Register (props){
    const users =[]
    const [flag, setFlag] = useState(false);
    const [login, setLogin] = useState(true);
    const [state, setState] = useState({
        email: "",
        password: "",
        fullname: "",
      });

    //   useEffect(() => {
    //     localStorage.setItem('DATA_USER', JSON.stringify(state));
    //   }, [state]);

      console.log(localStorage)



      const handleOnChange = (e) => {
        // setState here
        setState({
          ...state,
          [e.target.name]: e.target.value,
        })
      }

      const handleOnSubmit = (e) => {
        e.preventDefault();

        users.push(state)
        localStorage.setItem("DATA_USER", JSON.stringify(users))

        if (!state.email || !state.password ||!state.fullname ) {
          setFlag(true);
        } else {
          setFlag(false);
          localStorage.setItem("email", JSON.stringify(state.email));
          localStorage.setItem("email", JSON.stringify(state.email));
          localStorage.setItem("password", JSON.stringify(state.password));
          localStorage.setItem("fullname", JSON.stringify(state.fullname));
          console.log("Saved in Local Storage");

        }
          setLogin(!login);
      }
        function handleClick() {
          setLogin(!login);
      }


    return(
        <>

        <Modal {...props} >

        <div className="form-group">

            <h1>
                Register
            </h1>

            <Form onSubmit={handleOnSubmit}>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Control  onChange={handleOnChange} value={state.email} type="email" placeholder="Enter email" name='email'/>
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Control onChange={handleOnChange} value={state.password} type="password" placeholder="Password" name='password' />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicFullName">
                    <Form.Control onChange={handleOnChange} value={state.fullname}  type="text" placeholder="Full Name" name='fullname'/>
                </Form.Group>

                <Button onClick={props.onLogin} variant="danger" type="submit">
                    Register
                </Button>

                <p>
                <Button onClick={props.onLogin} variant="link">Already have an account? <span>Here</span></Button>
                </p>
            </Form>

         </div>    

        </Modal>



        </>


    );
}

export default Register
